mapper.select:
select count(user_id)
from members
where user_id = #{userId}

select user_id, user_name, admin
from members
where user_id = #{userId} and user_password = #{userPwd}

SELECT user_name, user_id, user_password, email_id, email_domain, join_date
FROM members
WHERE user_id = #{userId}

mapper.update:
UPDATE members
user_password = #{userPwd}, email_id = #{emailId}, email_domain = #{emailDomain},
WHERE user_id = #{userId}
