mapper.select:
SELECT qnano, user_id, subject, content
FROM qna

	WHERE ${key} like concat('%', #{word}, '%')


	ORDER BY ${orderBy} ${orderByDir}


	LIMIT #{offset}, #{countPerPage}

SELECT qnano, user_id, subject, content
FROM qna
WHERE qnano = #{qnano}

mapper.update:
UPDATE qna
SET
	subject = #{subject},
	content = #{content}
WHERE qnano = #{qnano}
